mapper.select:
select s.*, c.name from setmeal s left join category c on s.category_id = c.id


        and s.name like concat('%', #{name}, '%')


        and s.status = #{status}


        and s.category_id = #{categoryId}


order by s.create_time desc

mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
where id = #{id}
